var progress=(new signalR.HubConnectionBuilder).withUrl("/progressHub").build();progress.on("Progress",function(n,t){var i=t.toFixed(2);$("#progressHub").css("width",i+"%").attr("aria-valuenow",i).html(i+"%")});progress.on("Status",function(n,t){var i="bg-"+t;$("#progressHub").removeClass("bg-danger bg-warning bg-success bg-info");$("#progressHub").addClass(i)});progress.start().catch(function(n){return console.error(n.toString())});